    super(title, author, overview, lessons, course_id);
    this.state = {
      lessons: [],
      courseContent: "",
      title: ""
    }
  }

componentDidMount = () => {
  this.setState({lessons:this.props.lessons, courseContent: this.props.overview})
}

  render() {
    return(
      <div>
        I'm the Course Container Friends!
